import {FcDeleteDatabase} from "react-icons/fc"
import { useDispatch } from "react-redux";
import { remove } from "../redux/Slices/CartSlice";
import { toast } from "react-hot-toast";

const CartItem = ({item, itemIndex}) => {
  const dispatch = useDispatch();

  const removeFromCart = () => {
    dispatch(remove(item.id));
    toast.success("Item Removed");
  }

  return (
    <div class="container w-11/12 mx-auto">

      <div className="flex flex-row gap-4 w-[80%] border p-4">

        <div>
          <img src={item.image} className="w-[500px] h-[300px]"/>
        </div>
        <div>
          <h1 className="text-bold my-3">{item.title}</h1>
          <h1 className="my-3">{item.description}</h1>
          <div className="flex justify-be">
            <p className="text-3xl font-bold">${item.price}</p>
            <div
            onClick={removeFromCart} className="text-[red]">
              <FcDeleteDatabase className="text-red-600"/>
            </div>
          </div>

        </div>


      </div>

    </div>
  );
};

export default CartItem;
